void interpolation_search(int a[],int l,int r,int x)
{
    int mid;
    while(l<=r){
        mid=l+(r-l)*((x-a[l])/(a[r]-a[l]));
        if(x==a[mid]){
            printf("Element found at index %d",mid);
            return;
        }
        if(x<a[mid]){
            r=mid-1;
        }
        else{
            l=mid+1;
        }
    }
    printf("Element not found");
}
