#include<stdio.h>
void shell_sort(int a[],int n,int increments[], int numinc)
{
    int i,j,k,span,temp;
    for(k=0;k<numinc;k++)
    {
        span=increments[k];
        for(i=span;i<n;i++){
            temp=a[i];
            for(j=i-span;j>=0 && temp<a[j];j=j-span){
                a[j+span]=a[j];
            }
            a[j+span]=temp;
        }
    }
    for(i=0;i<n;i++)
    {
        printf("%d\t",a[i]);
    }
}
