#include<stdio.h>
void quick_sort(int a[],int low, int high)
{
    int i,j,temp,x;
    if(low>=high)
    return;
    else{
        x=a[low];
        i=low+1;
        j=high;
        while(i<=j){
        while(a[i]<x && i<=high){
            i++;
        }
        while(a[j]>x && j>low){
            j--;
        }
        if(i<j){
            temp=a[i];
            a[i]=a[j];
            a[j]=temp;
            i++;
            j--;
        }
        }
        a[low]=a[j];
        a[j]=x;

        quick_sort(a,low,j-1);
        quick_sort(a,j+1,high);
    }
}
